function parse_nested_parens(paren_string: string): number[] {
    const result: number[] = [];
    let current_level: number = 0;
    for (let i = 0; i < paren_string.length; i++) {
        if (paren_string[i] === '(') {
            current_level++;
        } else if (paren_string[i] === ')') {
            current_level--;
            if (current_level === 0) {
                result.push(current_level);
            }
        }
    }
    return result;
}
