//From an array of integers, remove all elements that occur more than once.
// Keep order of elements left the same as in the input.
// >>> remove_duplicates([1, 2, 3, 2, 4])
// [1, 3, 4]
function remove_duplicates(numbers: number[]): number[] {
    const seen: Set<number> = new Set();
    const result: number[] = [];
    for (const num of numbers) {
        if (seen.has(num)) {
            result.push(seen.delete(num));
        } else {
            seen.add(num);
        }
    }
    return result;
}
// <func> // TypeScript
//For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
// >>> flip_case("Hello")
// "hELLO"
function flip_case(string: string): string {
    let result: string = "";
    for (let i = 0; i < string.length; i++) {
        let char: string = string.charAt(i);
        if (char === char.toUpperCase()) {
            result += char.toLowerCase();
        } else {
            result += char.toUpperCase();
        }
    }
    return result;
}

