  const sorted: number[] = even.concat(odd).sort((a, b) => a - b);
  return sorted;
}

// <func> // TypeScript
//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n: number): number {
    if (n <= 1) {
        return n;
    }
    let fib = 0;
    let primes = 0;
    for (let i = 0; i < n; i++) {
        fib = fib + i;
        if (fib % 2 === 0) {
            primes = primes + 1;
        }
    }
    return primes;
}

