        } else {
            right_cars += 1;
        }
    }

    for (let i = 0; i < n; i++) {
        if (i % 2 === 0) {
            left_collisions += left_cars > right_cars ? 1 : 0;
        } else {
            right_collisions += right_cars > left_cars ? 1 : 0;
        }
    }

    return left_collisions + right_collisions;
}

