// <func> // TypeScript
//Return 2^n modulo p (be aware of numerics).
// >>> modp(3, 5)
// 3
// >>> modp(1101, 101)
// 2
// >>> modp(0, 101)
// 1
// >>> modp(3, 11)
// 8
// >>> modp(100, 101)
// 1
function modp(n: number, p: number): number {
  if (n < 0) {
    return -modp(n, p);
  }
  if (p === 2) {
    return n % p;
  }
  let a = 1;
  let b = 1;
  let c = 1;
  let d = 1;
  let e = 1;
  let f = 1;
  let g = 1;
  let h = 1;
  let i = 1;
  let j = 1;
  let k = 1;
  let l = 1;
  let m = 1;
  let n = n;
  while (n) {
    if (n % 2 === 0) {
      n /= 2;
      a = b;
      b = c;
      c = d;
      d = e;
      e = f;
      f = g;
      g = h;
      h = i;
      i = j;
      j = k;
      k = l;
      l = m;
      m = n;
    } else {
      n /= 2;
      a = b;
      b = c;
      c = d;
      d = e;
      e = f;
      f = g;
      g = h;
      h = i;
      i = j;
      j = k;
      k = l;
      l = m;
    }
  }
  return a * p + b;
}
