// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l: number[]): boolean {
    let i = 0;
    let j = l.length - 1;
    while (i < j) {
        if (l[i] > l[j]) {
            return true;
        }
        if (l[i] < l[j]) {
            return false;
        }
        i++;
        j--;
    }
    return false;
}

