// 29
// >>> largest_prime_factor(2048)
// 2
function largest_prime_factor(n: number): number {
    if (n <= 1) {
        return 1;
    }
    let largest_factor = 1;
    for (let i = 2; i <= Math.sqrt(n); i++) {
        if (n % i === 0) {
            if (i * i > n) {
                return largest_factor;
            }
            largest_factor = i;
        }
    }
    return largest_factor;
}
