// Please write a function to efficiently compute the n-th element of the fibfib number sequence.
// >>> fibfib(1)
// 0
// >>> fibfib(5)
// 4
// >>> fibfib(8)
// 24
function fibfib(n: number): number {
    if (n <= 0) {
        return 0;
    }
    if (n === 1) {
        return 0;
    }
    if (n === 2) {
        return 1;
    }
    let result = 0;
    let a = 0;
    let b = 0;
    let c = 0;
    for (let i = 0; i < n; i++) {
        result += a;
        result += b;
        result += c;
        a = b;
        b = c;
        c = result;
    }
    return result;
}
