// false
// >>> is_simple_power(3, 1)
// false
// >>> is_simple_power(5, 3)
// false
function is_simple_power(x: number, n: number): boolean {
    if (n % 2 === 0) {
        return false;
    }
    let count = 0;
    for (let i = 1; i <= Math.sqrt(x); i++) {
        if (x % i === 0) {
            count++;
        }
    }
    return count === 2;
}

