            distinct = false;
            break;
        }
    }
    if (distinct) {
        for (let i = 0; i < length - 2; i++) {
            if (s[i] === s[i + 1] || s[i] === s[i + 2]) {
                return false;
            }
        }
        return true;
    }
    return false;
}

