// >>> get_row([], 1)
// []
// >>> get_row([[], [1], [1, 2, 3]], 3)
// [[2, 2]]
function get_row(lst: number[][], x: number): [number, number][] {
    const rows: number[] = [];
    const cols: number[] = [];
    for (let i = 0; i < lst.length; i++) {
        const row: number[] = [];
        for (let j = 0; j < lst[i].length; j++) {
            row.push(lst[i][j]);
        }
        rows.push(row);
        cols.push(i);
    }
    const sorted_rows: number[] = rows.sort((a, b) => b - a);
    const sorted_cols: number[] = cols.sort((a, b) => b - a);
    const sorted_row: number[] = sorted_rows[x];
    const sorted_col: number[] = sorted_cols[x];
    return [sorted_row, sorted_col];
}

