// >>> sort_array([2, 4, 3, 0, 1, 5, 6])
// [6, 5, 4, 3, 2, 1, 0]
function sort_array(array: number[]): number[] {
    if (array.length === 0) {
        return [];
    }
    if (array.length === 1) {
        return array;
    }
    let odd = [];
    let even = [];
    for (let i = 0; i < array.length; i++) {
        if (array[i] % 2 === 1) {
            odd.push(array[i]);
        } else {
            even.push(array[i]);
        }
    }
    return sort_array(odd) + even;
}
