["hI", "hi", "hi"]
>>> total_match(["4"], ["1", "2", "3", "4", "5"])
["4"]
*/
string[] total_match(string[] lst1, string[] lst2) {
    int len1 = lst1.length;
    int len2 = lst2.length;
    if (len1 == 0 || len2 == 0) {
        return lst1;
    }
    int len = Math.min(len1, len2);
    int i = 0;
    while (i < len) {
        if (lst1[i].length < lst2[i].length) {
            return lst1;
        }
        if (lst1[i].length > lst2[i].length) {
            return lst2;
        }
        if (lst1[i].length == lst2[i].length) {
            if (lst1[i] < lst2[i]) {
                return lst1;
            }
            if (lst1[i] > lst2[i]) {
                return lst2;
            }
        }
        i++;
    }
    return lst1;
}

