bool will_it_fly(long[] q, long w) {
    if (q.length == 0) {
        return false;
    }
    if (q.length == 1) {
        return q[0] == 0;
    }
    if (q.length == 2) {
        if (q[0] == q[1]) {
            return true;
        } else {
            return false;
        }
