long do_algebra(string[] operator, long[] operand) {
    long result = 0;
    for (int I = 0; I < operator.length - 1; i++) {
        switch (operator[i]) {
            case '+':
                result += operand[i];
                break;
            case '-':
                result -= operand[i];
                break;
            case '*':
                result *= operand[i];
                break;
            case '/':
                if (operand[i] == 0) {
                    throw std.runtime_error("Division by zero");
                }
                result /= operand[i];
                break;
            case '^':
                result = std.math.pow(operand[i], result);
                break;
            default:
                throw std.runtime_error("Invalid operator");
        }
    }
    return result;
}

