        return s;
    }
    string res = "";
    for (int I = 0; I < s.length; i++) {
        char c = s[i];
        if (c >= 'a' && c <= 'z') {
            if (c == 'a') {
                res += 'A';
            } else {
                res += 'a';
            }
        } else if (c >= 'A' && c <= 'Z') {
            if (c == 'A') {
                res += 'a';
            } else {
                res += 'A';
            }
        } else {
            res += c;
        }
    }
    return res;
}
