# For a given number n, find the largest number that divides n evenly, smaller than n
# >>> largest_divisor(15)
# 5
largest_divisor <- function(n) {
  # Find the largest number that divides n evenly
  largest <- 1
  for (i in 2:n) {
    if (i % n == 0) {
      largest <- max(largest, i)
    }
  }
  largest
}

# Return list of prime factors of given integer in the order from smallest to largest.
# Each of the factors should be listed number of times corresponding to how many times it appeares in factorization.
# Input number should be equal to the product of all factors
# >>> factorize(8)
# c(2, 2, 2)
# >>> factorize(25)
# c(5, 5)
# >>> factorize(70)
# c(2, 5, 7)
factorize <- function(n) {
  # Check if n is prime
  if (n <= 1) {
    return(c(0, 1))
  } else if (n == 2) {
    return(c(1))
  } else if (n % 2 == 0) {
    return(c(0, 1))
  } else {
    # Find factors of n
    factors <- factor(n)
    # Find prime factors in order from smallest to largest
    factors <- factors[order(factors, decreasing = TRUE)]
    # Return list of prime factors in the order from smallest to largest
    return(c(length(factors), factors))
  }
}

# From a list of integers, remove all elements that occur more than once.
# Keep order of elements left the same as in the input.
# >>> remove_duplicates(c(1, 2, 3, 2, 4))
# c(1, 3, 4)
remove_duplicates <- function(numbers) {
  # Create a list of all unique elements
  unique_numbers <- unique(numbers)

  # Keep the order of the elements left the same as in the input
  sorted_numbers <- sort(unique_numbers)

  # Remove all elements that occur more than once
  filtered_numbers <- c()
  for (i in 1:length(sorted_numbers)) {
    if (length(unique_numbers[i]) > 1) {
      filtered_numbers <- c(filtered_numbers, sorted_numbers[i])
    }
  }

  # Return the filtered list
  filtered_numbers
}

# For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
# >>> flip_case('Hello')
# 'hELLO'
flip_case <- function(string) {
  # Split string into an array of characters
  char_array <- strsplit(string, "")

  # Loop through each character in the array
  for (i in 1:length(char_array)) {
    # If the character is lowercase, swap it with the uppercase version
    if (char_array[i] %in% c("a", "e", "i", "o", "u")) {
      char_array[i] <- toupper(char_array[i])
    }

    # If the character is uppercase, swap it with the lowercase version
    else if (char_array[i] %in% c("A", "E", "I", "O", "U")) {
      char_array[i] <- lowercase(char_array[i])
    }
  }

  # Join the array back into a single string
  paste(char_array, collapse = " ")
}

# Filter an input list of strings only for ones that start with a given prefix.
# >>> filter_by_prefix(c(), 'a')
# c()
# >>> filter_by_prefix(c('abc', 'bcd', 'cde', 'array'), 'a')
# c('abc', 'array')
filter_by_prefix <- function(strings, prefix) {
  filter(strings, function(x) {
    return startsWith(x, prefix)
  })
}

# Return true if a given number is prime, and false otherwise.
# >>> is_prime(6)
# FALSE
# >>> is_prime(101)
# TRUE
# >>> is_prime(11)
# TRUE
# >>> is_prime(13441)
# TRUE
# >>> is_prime(61)
# TRUE
# >>> is_prime(4)
# FALSE
# >>> is_prime(1)
# FALSE
is_prime <- function(n) {
  for (i in 2:n) {
    if (n % i == 0) {
      return(FALSE)
    }
  }
  return(TRUE)
}

# Return maximum element in the list.
# >>> max_element(c(1, 2, 3))
# 3
# >>> max_element(c(5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10))
# 123
max_element <- function(l) {
  max <- l[1]
  for (i in 2:length(l)) {
    if (l[i] > max) {
      max <- l[i]
    }
  }
  max
}

# Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
# >>> fizz_buzz(50)
# 0
# >>> fizz_buzz(78)
# 2
# >>> fizz_buzz(79)
# 3
fizz_buzz <- function(n) {
  if (n % 11 == 0 || n % 13 == 0) {
    return(1)
  } else {
    return(0)
  }
}

# This function takes a list l and returns a list l' such that
# l' is identical to l in the odd indicies, while its values at the even indicies are equal
# to the values of the even indicies of l, but sorted.
# >>> sort_even(c(1, 2, 3))
# c(1, 2, 3)
# >>> sort_even(c(5, 6, 3, 4))
# c(3, 6, 5, 4)
sort_even <- function(l) {
  n <- length(l)
  if (n %in% c(0, 2)) {
    l
  } else {
    l[n %in% c(0, 2)] <- sort(l[n %in% c(0, 2)])
    l[n %in% c(1, 3, 5, 7)] <- sort(l[n %in% c(1, 3, 5, 7)])
    l
  }
}

# prime_fib returns n-th number that is a Fibonacci number and it's also prime.
# >>> prime_fib(1)
# 2
# >>> prime_fib(2)
# 3
# >>> prime_fib(3)
# 5
# >>> prime_fib(4)
# 13
# >>> prime_fib(5)
# 89
prime_fib <- function(n) {
  if (n < 2) {
    return(n)
  } else if (n % 2 == 0) {
    return(n)
  } else {
    return(prime_fib(n-2) + prime_fib(n-1))
  }
}

