# Return list with elements incremented by 1.
# >>> incr_list(c(1, 2, 3))
# c(2, 3, 4)
# >>> incr_list(c(5, 3, 5, 2, 3, 3, 9, 0, 123))
# c(6, 4, 6, 3, 4, 4, 10, 1, 124)
incr_list <- function(l) {
  n <- length(l)
  res <- rep(1, n)
  for (i in 1:n) {
    res[i] <- l[i] + 1
  }
  res
}

# pairs_sum_to_zero takes a list of integers as an input.
# it returns TRUE if there are two distinct elements in the list that
# sum to zero, and FALSE otherwise.
# >>> pairs_sum_to_zero(c(1, 3, 5, 0))
# FALSE
# >>> pairs_sum_to_zero(c(1, 3, -2, 1))
# FALSE
# >>> pairs_sum_to_zero(c(1, 2, 3, 7))
# FALSE
# >>> pairs_sum_to_zero(c(2, 4, -5, 3, 5, 7))
# TRUE
# >>> pairs_sum_to_zero(c(1))
# FALSE
pairs_sum_to_zero <- function(l) {
  n <- length(l)
  if (n == 1) {
    return(TRUE)
  } else if (all(l %in% c(0, -1))) {
    return(TRUE)
  } else if (any(l %in% c(0, -1))) {
    return(FALSE)
  } else {
    sum_to_zero <- sum(l)
    for (i in 2:n) {
      if (sum(l[1:i]) == sum_to_zero) {
        return(TRUE)
      }
    }
    return(FALSE)
  }
}

# Change numerical base of input number x to base.
# return string representation after the conversion.
# base numbers are less than 10.
# >>> change_base(8, 3)
# '22'
# >>> change_base(8, 2)
# '1000'
# >>> change_base(7, 2)
# '111'
change_base <- function(x, base) {
  if (base == 1) {
    return(x)
  } else {
    return(base * change_base(x / base, base - 1))
  }
}

