# >>> fib(1)
# 1
# >>> fib(8)
# 21
fib <- function(n) {
  if (n < 2) {
    return(n)
  } else {
    return(fib(n-1) + fib(n-2))
  }
}

# brackets is a string of "<" and ">".
# return TRUE if every opening bracket has a corresponding closing bracket.
# >>> correct_bracketing('<')
# FALSE
# >>> correct_bracketing('<>')
# TRUE
# >>> correct_bracketing('<<><>>')
# TRUE
# >>> correct_bracketing('><<>')
# FALSE
correct_bracketing <- function(brackets) {
  if (length(brackets) == 0) {
    return(TRUE)
  }
  if (brackets[1] == "<") {
    return(correct_bracketing(brackets[2:length(brackets)-1]))
  }
  if (brackets[length(brackets) - 1] == ">") {
    return(correct_bracketing(brackets[1:length(brackets)-2]))
  }
  return(FALSE)
}

# Return TRUE is list elements are monotonically increasing or decreasing.
# >>> monotonic(c(1, 2, 4, 20))
# TRUE
# >>> monotonic(c(1, 20, 4, 10))
# FALSE
# >>> monotonic(c(4, 1, 0, -10))
# TRUE
monotonic <- function(l) {
  n <- length(l)
  for (i in 1:n) {
    if (i > 1) {
      if (l[i] < l[i-1]) {
        return(FALSE)
      }
    }
    if (i < n-1) {
      if (l[i] > l[i+1]) {
        return(FALSE)
      }
    }
  }
  return(TRUE)
}

