# vowel, but only when it is at the end of the given word.
# Example:
# >>> vowels_count('abcde')
# 2
# >>> vowels_count('ACEDY')
# 3
vowels_count <- function(s) {
  count <- 0
  for (i in 1:length(s)) {
    if (s[i] == "a" || s[i] == "e" || s[i] == "i" || s[i] == "o" || s[i] == "u") {
      count <- count + 1
    }
  }
  return(count)
}

