# if the two lists have the same number of chars, return the first list.
# Examples
# >>> total_match(c(), c())
# c()
# >>> total_match(c('hi', 'admin'), c('hI', 'Hi'))
# c('hI', 'Hi')
# >>> total_match(c('hi', 'admin'), c('hi', 'hi', 'admin', 'project'))
# c('hi', 'admin')
# >>> total_match(c('hi', 'admin'), c('hI', 'hi', 'hi'))
# c('hI', 'hi', 'hi')
# >>> total_match(c('4'), c('1', '2', '3', '4', '5'))
# c('4')
total_match <- function(lst1, lst2) {
  if (length(lst1) != length(lst2)) {
    return(lst1)
  } else {
    total_chars <- 0
    for (i in 1:length(lst1)) {
      total_chars <- total_chars + length(lst1[i])
    }
    total_chars_lst <- c()
    for (i in 1:length(lst2)) {
      total_chars_lst <- c(total_chars_lst, length(lst2[i]))
    }
    total_chars_lst <- total_chars_lst[order(total_chars_lst)]
    return(lst1[which.min(total_chars_lst)])
  }
}

