# TRUE
# >>> is_happy('aabb')
# FALSE
# >>> is_happy('adb')
# TRUE
# >>> is_happy('xyy')
# FALSE
is_happy <- function(s) {
  n <- length(s)
  if (n < 3) return(FALSE)
  if (n == 3) {
    if (all(s[1:2] %in% s[3:4])) return(TRUE)
    return(FALSE)
  }
  for (i in 3:n) {
    if (i > 2 && s[i] != s[i-1] && s[i] != s[i-2]) {
      return(FALSE)
    }
  }
  return(TRUE)
}

