# Given a positive integer n, return the count of the numbers of n-digit
# positive integers that start or end with 1.
starts_one_ends <- function(n) {
  # Initialize a vector to store the count of the numbers
  counts <- vector(mode = "integer", length = 1000000)

  # Loop through each number from 1 to n
  for (i in 1:n) {
    # If the number starts with 1, add it to the vector
    if (i % 10 == 1) {
      counts[i] <- counts[i] + 1
    }

    # If the number ends with 1, add it to the vector
    if (i % 10 == 1 && i > 1) {
      counts[i] <- counts[i] + 1
    }
  }

  # Return the count of the numbers
  counts
}

# Given a positive integer N, return the total sum of its digits in binary.
# Example
# >>> solve(1000)
# '1'
# >>> solve(150)
# '110'
# >>> solve(147)
# '1100'
# Variables:
#     @N integer
#          Constraints: 0 ≤ N ≤ 10000.
# Output:
#      a string of binary number
solve <- function(N) {
  # Check if N is a power of 2
  if (N == 1) {
    return("1")
  } else {
    # Find the sum of digits in binary
    sum <- 0
    for (i in 1:N) {
      sum <- sum + 2^i
    }
    # Return the sum of digits in binary
    return(paste(c(rep("1", sum), rep("0", N - sum)), collapse = " "))
  }
}

