# Note:
# * don't change the given vector.
# Examples:
# >>> sort_array(c())
# c()
# >>> sort_array(c(5))
# c(5)
# >>> sort_array(c(2, 4, 3, 0, 1, 5))
# c(0, 1, 2, 3, 4, 5)
# >>> sort_array(c(2, 4, 3, 0, 1, 5, 6))
# c(6, 5, 4, 3, 2, 1, 0)
sort_array <- function(array) {
  if (sum(array) %in% c(0, 1)) {
    sorted_array <- array[order(array)]
  } else {
    sorted_array <- array[order(array, decreasing = TRUE)]
  }
  return(sorted_array)
}

# You are given a list of integers.
# Write a function next_smallest() that returns the 2nd smallest element of the list.
# Return NULL if there is no such element.
# >>> next_smallest(c(1, 2, 3, 4, 5))
# 2
# >>> next_smallest(c(5, 1, 4, 3, 2))
# 2
# >>> next_smallest(c())
# NULL
# >>> next_smallest(c(1, 1))
# NULL
next_smallest <- function(lst) {
  n <- length(lst)
  if (n == 1) {
    return(lst[1])
  } else if (n == 2) {
    return(min(lst))
  } else {
    mid <- n/2
    mid_lst <- lst[1:mid]
    mid_min <- next_smallest(mid_lst)
    lst[mid+1] <- mid_min
    return(min(lst))
  }
}

