# >>> by_length(c(1, -1, 55))
# c('One')
by_length <- function(arr) {
  arr <- as.character(arr)
  arr <- str_replace_all(arr, "\\d+", function(x) {
    if (x %in% c("One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine")) {
      x <- paste0(x, " ")
    }
    x
  })
  arr <- str_replace_all(arr, "\\d+", function(x) {
    if (x %in% c("One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine")) {
      x <- paste0(x, " ")
    }
    x
  })
  arr <- str_replace_all(arr, "\\d+", function(x) {
    if (x %in% c("One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine")) {
      x <- paste0(x, " ")
    }
    x
  })
  arr
}

