# Note:
#     1. 1 <= n <= 10^3
#     2. returned list has the number of even and odd integer palindromes respectively.
even_odd_palindrome <- function(n) {
  if (n < 1 || n > 10^3) {
    return(c(0, 0))
  }

  # check if the number is even or odd
  if (n % 2 == 0) {
    return(c(1, length(even_odd_palindrome(n/2)) + 1))
