# list('cdedc', TRUE)
reverse_delete <- function(s, c) {
  n <- length(s)
  m <- length(c)
  result <- c(rep(NA, n), rep(NA, m))
  for (i in 1:n) {
    for (j in 1:m) {
      if (s[i] == c[j]) {
        result[i] <- c(result[i], s[i])
        result[j] <- c(result[j], c[j])
      }
    }
  }
  result
}

