# Given a list of numbers, return the sum of squares of the numbers
# in the list that are odd. Ignore numbers that are negative or not integers.
# >>> double_the_difference(c(1, 3, 2, 0))
# 10
# >>> double_the_difference(c(-1, -2, 0))
# 0
# >>> double_the_difference(c(9, -2))
# 81
# >>> double_the_difference(c(0))
# 0
# If the input list is empty, return 0.
double_the_difference <- function(lst) {
  if (length(lst) < 2) {
    return(0)
  }
  sum_of_squares <- sum(abs(lst))
  for (i in 1:length(lst)) {
    if (is.integer(lst[i])) {
      sum_of_squares[i] <- sum_of_squares[i] * 2
    } else {
      sum_of_squares[i] <- sum_of_squares[i] * 3
    }
  }
  return(sum_of_squares)
}

