}

# Given an integer. return a list that has the number of even and odd digits respectively.
# Example:
#     >>> even_odd_count(-12)
#     c(1, 1)
#     >>> even_odd_count(123)
#     c(1, 2)
even_odd_count <- function(num) {
  if (num < 0) {
    return(c(1, 1))
  } else {
    num_even <- num %/% 2 == 0
    num_odd <- num %/% 2 != 0
    return(c(num_even, num_odd))
  }
}

# Given a positive integer, obtain its roman numeral equivalent as a string,
# and return it in lowercase.
# Restrictions: 1 <= num <= 1000
# Examples:
# >>> int_to_mini_roman(19)
# 'xix'
# >>> int_to_mini_roman(152)
# 'clii'
# >>> int_to_mini_roman(426)
# 'cdxxvi'
int_to_mini_roman <- function(number) {
  if (number < 1000) {
    return(paste(number, collapse = " "))
  } else {
    # Convert to lowercase
    number <- number %in% c("I", "V", "X", "L", "C", "D", "M")
    # Convert to roman numeral
    roman <- c("I", "V", "X", "L", "C", "D", "M")
    # Convert to string
    return(paste(roman[number], collapse = " "))
  }
}

