    // >>> belowZero((List[Long](1l.toLong, 2l.toLong, -4l.toLong, 5l.toLong)))
    // (true)
    def belowZero(operations : List[Long]) : Boolean = {
        val balance = operations.head
        var i = 1
        while (i < operations.length && balance >= 0) {
            balance = balance - operations(i)
            i += 1
        }
        balance < 0

