    def allPrefixes(string : String) : List[String] = {
        val prefixes = List.empty[String]
        val prefixLengths = List.empty[Int]
        var I = 0
        while (i < string.length) {
            val j = string.indexOf(string(i), i)
            if (j == -1) {
                prefixes = prefixes :+ string.substring(i, i + 1)
                prefixLengths = prefixLengths :+ 1
                i = j + 1
            } else {
                prefixes = prefixes :+ string.substring(i, j)
                prefixLengths = prefixLengths :+ j - i + 1
                i = j + 1
            }
        }
        prefixes :+ string.substring(i, string.length)

