    // Each of the factors should be listed number of times corresponding to how many times it appeares in factorization.
    // Input number should be equal to the product of all factors
    // >>> factorize((8l))
    // (List[Long](2l.toLong, 2l.toLong, 2l.toLong))
    // >>> factorize((25l))
    // (List[Long](5l.toLong, 5l.toLong))
    // >>> factorize((70l))
    // (List[Long](2l.toLong, 5l.toLong, 7l.toLong))
    def factorize(n : Long) : List[Long] = {
        val factors = List.fill(scala.math.max(scala.math.sqrt(n).toInt, 1))(1)
        val factorsList = factors.toList
        var I = 2
        while (i < factorsList.length) {
            if (factorsList(i) * factorsList(i) == n) {
                factorsList(i) = 1
            } else {
                factorsList(i) = factorsList(i) * factorsList(i)
            }
            I += 1
        }
        factorsList.reverse

