    // >>> isPrime((4l))
    // (false)
    // >>> isPrime((1l))
    // (false)
    def isPrime(n : Long) : Boolean = {
        if (n == 1) false
        else if (n == 2) true
        else if (n % 2 == 0) false
        else {
            var I = 3
            while (i * I <= n) {
                if (n % I == 0) return false
                i += 2
            }
            return true
        }

