object Problem {
    // Given a string 'text', return its md5 hash equivalent string.
    // If 'text' is an empty string, return None.
    // >>> stringToMd5(("Hello world"))
    // Some("3e25960a79dbc69b674cd4ec67a72c62")
    def stringToMd5(text : String) : Option[String] = {
        if (text.isEmpty) None else {
            val md5 = java.security.MessageDigest.getInstance("MD5")
            val bytes = text.getBytes("UTF-8")
            md5.update(bytes)
            val digest = md5.digest()
            val hexDigits = "0123456789ABCDEF".charAt(_.toDigit)
            val result = for (i <- 0 until digest.length) yield {
                val b = digest(i)
                if (b < 0) b + 256 else b.toHexString.toChar
            }.mkString("")
            Some(result.reverse.take(16).reverse.mkString(""))
        }

