/// >>> parse_music(music_string: "o o| .| o| o| .| .| .| .| o o")
/// [4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4]
func parse_music(music_string: String) -> [Int] {
    let notes = music_string.components(separatedBy: "|")
    let notes_array = notes.map { $0.characters.map { $0.decomposed()?.toInt() ?? 0 } }
    return notes_array

