func minSubArraySum(nums: [Int]) -> Int {
    var minSum = Int.maxInitialValue
    var minIndex = Int.maxInitialValue
    for num in nums {
        let sum = num + minSum
        if sum < 0 {
            minSum = num
            minIndex = 0
        } else if sum == 0 {
            minIndex = 0
        } else {
