/// 0
/// Constraints:
/// * all wells have the same length
/// * 1 <= grid.length <= 10^2
/// * 1 <= grid[:,1].length <= 10^2
/// * grid[i][j] -> 0 | 1
/// * 1 <= capacity <= 10
func max_fill(grid: [[Int]], capacity: Int) -> Int {
    let n = grid.count
    let m = grid[0].count
    let buckets = [Int](repeating: 0, count: m)
    let max_fill = 0
    for I in 0..<n {
        for j in 0..<m {
            if grid[i][j] == 1 {
                buckets[grid[i][j]] += 1
                if buckets[grid[i][j]] == capacity {
                    max_fill += 1
                }
            }
        }
    }
    return max_fill

