    case intValue(Int)
    case doubleValue(Double)
    case stringValue(String)
}


/// Create a function that takes integers, floats, or strings representing
/// real numbers, and returns the larger variable in its given variable type.
/// Return nil if the values are equal.
/// Note: If a real number is represented as a string, the floating point might be . or ,
/// >>> compare_one(a: .intValue(1), b: .doubleValue(2.5))
/// .doubleValue(2.5)
/// >>> compare_one(a: .intValue(1), b: .stringValue("2,3"))
/// .stringValue("2,3")
/// >>> compare_one(a: .stringValue("5,1"), b: .stringValue("6"))
/// .stringValue("6")
/// >>> compare_one(a: .stringValue("1"), b: .intValue(1))
/// nil
func compare_one(a: Value, b: Value) -> Value? {
    switch a {
    case .intValue(let aInt):
        switch b {
        case .intValue(let bInt):
            return aInt > bInt ? aInt : bInt
        default:
            return nil
        }
    case .doubleValue(let aDouble):
        switch b {
        case .doubleValue(let bDouble):
            return aDouble > bDouble ? aDouble : bDouble
        default:
            return nil
        }
    case .stringValue(let aString):
        switch b {
        case .stringValue(let bString):
            return aString.compare(bString) ?? .stringValue(nil)
        default:
            return nil
        }
    }

