/// [-1, -11, 1, -12, 11]
/// >>> order_by_points(nums: [] as [Int])
/// [] as [Int]
func order_by_points(nums: [Int]) -> [Int] {
    var sorted = [Int]()
    var sum = 0
    for num in nums {
        sum += num
    }
    sorted.append(sum)
    sorted.sort()
    return sorted

/// Write a function that takes an array of numbers as input and returns 
