/// [4, 4, 1, 0, 0, 6]
func compare(game: [Int], guess: [Int]) -> [Int] {
    var result: [Int] = []
    for I in 0..<game.count {
        let score = game[i]
        let guessScore = guess[i]
        if score == guessScore {
            result.append(0)
        } else {
            result.append(abs(score - guessScore))
        }
    }
    return result

