        } else {
            odd += 1
        }
    }
    return (even, odd)

/// Given a positive integer, obtain its roman numeral equivalent as a string,
/// and return it in lowercase.
/// Restrictions: 1 <= num <= 1000
/// Examples:
/// >>> int_to_mini_roman(number: 19)
/// "xix"
/// >>> int_to_mini_roman(number: 152)
/// "clii"
/// >>> int_to_mini_roman(number: 426)
/// "cdxxvi"
func int_to_mini_roman(number: Int) -> String {
    var result = ""
    let num = number
    let min = Int(num.floor(to: 1000))
    let max = Int(num.ceil(to: 1000))
    for I in min..<max {
        let roman = String(i)
        let roman_string = roman.lowercased()
        let roman_lowercase = roman_string.lowercased()
        if roman_lowercase == roman_string {
            result += roman_lowercase
        } else {
            result += " " + roman_lowercase
        }
    }
    return result

