func right_angle_triangle(a: Int, b: Int, c: Int) -> Bool {
    let s1 = sqrt(a * a + b * b)
    let s2 = sqrt(c * c + b * b)
    let s3 = sqrt(a * a + c * c)

    if s1 == s2 && s1 == s3 {
        return true
    } else if s1 == s2 && s2 == s3 {
        return true
    } else if s1 == s3 && s2 == s3 {
        return true
    } else {
        return false
    }

