object Problem {
    // Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
    // separate those group into separate strings and return the list of those.
    // Separate groups are balanced (each open brace is properly closed) and not nested within each other
    // Ignore any spaces in the input string.
    // >>> separateParenGroups(("( ) (( )) (( )( ))"))
    // (List[String]("()", "(())", "(()())"))
    def separateParenGroups(paren_string : String) : List[String] = {
        val groups = paren_string.split("\\s+")
        val result = new ListBuffer[String]
        groups.foreach {
            case (group, next_group) =>
                val next_group_string = next_group.split("\\s+")
                val next_group_list = new ListBuffer[String]
                next_group_list.append(next_group_string(0))
                next_group_list.append(next_group_string(1))
                result.append(next_group_list.mkString(" "))
        }
        result.toList

