    // >>> howManyTimes((""), ("a"))
    // (0l)
    // >>> howManyTimes(("aaa"), ("a"))
    // (3l)
    // >>> howManyTimes(("aaaa"), ("aa"))
    // (3l)
    def howManyTimes(string : String, substring : String) : Long = {
        val result = new mutable.Long()
        var i = 0
        while (i < string.length) {
            var j = 0
            while (j < substring.length) {
                if (string(i) == substring(j)) {
                    result.set(result.getOrElse(result.getAndAdd(1)), i)
                    i += 1
                } else {
                    j += 1
                }
            }
            i += 1
        }
        result.getAndAdd(1)

