//Given a positive floating point number, it can be decomposed into
// and integer part (largest integer smaller than given number) and decimals
// (leftover part always smaller than 1).
// Return the decimal part of the number.
// >>> truncate_number(3.5)
// 0.5
function truncate_number(number: number): number {
  if (number < 0) {
    return number;
  }
  var result = number;
  var remainder = number % 1;
  if (remainder === 0) {
    return result;
  }
  var decimals = remainder / 100;
  var fraction = remainder % 100;
  if (fraction === 0) {
    return result;
  }
  var decimal = fraction / 10;
  if (decimal === 0) {
    return result;
  }
  result = result - decimal * 10;
  return result;
}

