// >>> get_row([], 1)
// []
// >>> get_row([[], [1], [1, 2, 3]], 3)
// [[2, 2]]
function get_row(lst: number[][], x: number): [number, number][] {
  let row = 0;
  let col = 0;
  let row_count = lst.length;
  let col_count = lst[0].length;

  for (let i = 0; i < row_count; i++) {
    for (let j = 0; j < col_count; j++) {
      if (lst[i][j] === x) {
        row++;
        col++;
      }
    }
  }

  return row, col;
}


