// >>> sort_array([2, 4, 3, 0, 1, 5, 6])
// [6, 5, 4, 3, 2, 1, 0]
function sort_array(array: number[]): number[] {
    if (array.length === 0) {
        return array;
    }
    var result: number[] = [];
    var i: number = 0;
    var j: number = array.length - 1;
    while (i <= j) {
        var temp: number = array[i];
        i++;
        j--;
        if (temp % 2 === 0) {
            result.push(array[i]);
        }
    }
    return result;
}


