// >>> count_nums([])
// 0
// >>> count_nums([-1, 11, -11])
// 1
// >>> count_nums([1, 1, 2])
// 3
function count_nums(arr: number[]): number {
  return arr.reduce((acc, num) => {
    if (num < 0) {
      acc += -1;
    } else {
      acc += num;
    }
    return acc;
  }, 0);
}

