//You are given an array arr of integers and you need to return
// sum of magnitudes of integers multiplied by product of all signs
// of each number in the array, represented by 1, -1 or 0.
// Note: return undefined for empty arr.
// Example:
// >>> prod_signs([1, 2, 2, -4])
// 9
// >>> prod_signs([0, 1])
// 0
// >>> prod_signs([])
// undefined
function prod_signs(arr: number[]): number | undefined {
  if (arr.length === 0) {
    return undefined;
  }

  const sign = arr.reduce((acc, num) => {
    if (num === 0) {
      return acc;
    }

    const sign_num = sign * num;
    const sign_sign = sign_num % 2 === 0;

    return acc + sign_sign ? sign_sign : sign_num;
  }, 0);

  return sign;
}

