// >>> solve("#a@C")
// "#A@c"
function solve(s: string): string {
    var result: string = "";
    var i: number = 0;
    var len: number = s.length;
    var letter: string = "";
    var case: string = "";
    var caseLetter: string = "";
    while (i < len) {
        caseLetter = s.charAt(i);
        case = caseLetter.toUpperCase();
        if (case == letter) {
            result += caseLetter;
            i++;
        } else {
            result += caseLetter;
            i++;
            caseLetter = case.toLowerCase();
        }
    }
    return result;
}


